// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function Details({ cityName, minTemp, maxTemp, image }) {
  // Zugriff auf die API-Schlüssel aus den Umgebungsvariablen
  const { REACT_APP_API_KEY } = process.env;

  // Ermitteln der Wochentage
  let daysOfWeek = ["So", "Mo", "Di", "Mi", "Do", "Fr", "Sa"];
  let today = new Date();
  let todayTrimed = today.toString().slice(0, 2);
  let currentIndex = daysOfWeek.indexOf(todayTrimed);

  let day2 = daysOfWeek[(currentIndex + 1) % 7];
  let day3 = daysOfWeek[(currentIndex + 2) % 7];
  let day4 = daysOfWeek[(currentIndex + 3) % 7];
  let day5 = daysOfWeek[(currentIndex + 4) % 7];
  let day6 = daysOfWeek[(currentIndex + 5) % 7];
  let day7 = daysOfWeek[(currentIndex + 6) % 7];

  //console.log(cityName);
  const apiURL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

  axios
    .get(apiURL)
    .then((res) => {
      console.log(res.data.list[12].dt_txt);
      let apiDate = new Date(res.data.list[0].dt_txt);
      // Hole den Wochentag des apiDate
      let apiDayIndex = apiDate.getDay();
      let apiDayOfWeek = daysOfWeek[apiDayIndex];

      // Hole den Wochentag des today
      let todayIndex = today.getDay();
      let todayOfWeek = daysOfWeek[todayIndex];

      // Vergleiche die Wochentage
      if (apiDayOfWeek === todayOfWeek) {
        console.log("Die Daten haben dasselbe Wochentag.");
      } else {
        console.log("Die Daten haben unterschiedliche Wochentage.");
      }
    })
    .catch((err) => {
      console.error("Fehler beim Abrufen der Wetterdaten:", err);
    });

  return (
    <>
      <div className="container_temp_infos">
        <h3>5-Tage-Vorhersage</h3>
        <div className="container_forecast">
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
          <div className="devider_forcast"></div>
          <div className="forecast_row">
            <p>{day2}</p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p>{day3}</p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p>{day4}</p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p>{day5}</p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p>{day6}</p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p>{day7}</p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
        </div>
        <div id="tab"></div>
      </div>
    </>
  );
}

export default Details;
