import "../style.css";
import Cloud from "./assets/cloud.png";
import Humidity from "./assets/weather.png";
import Wind from "./assets/wind.png";
import User from "./assets/user.png";
import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  const [data, setData] = useState(
    {
      celsius: 10,
      name: "London",
      humidity: 10,
      speed: 2,
      feel: 10,
    },
    []
  );

  useEffect(() => {
    const apiURL =
      "https://api.openweathermap.org/data/2.5/weather?q=berlin&appid=95e1808808fc4f2e2fac242dc555bb96&units=metric&lang=de";

    axios
      .get(apiURL)
      .then((res) => {
        console.log(res.data);
        setData({
          ...data, // kopiere vorhandene daten und aktualisiere
          celsius: res.data.main.temp,
          name: res.data.name,
          humidity: res.data.main.humidity,
          speed: res.data.wind.speed,
          feel: res.data.main.feels_like,
        });
      })
      .catch((err) => console.log(err));
    console.log(data);
  }, []);

  return (
    <div className="container">
      <div className="weather">
        <div className="search">
          <input type="text" placeholder="Enter City Name" />
          <button>
            <i className="fa-solid fa-magnifying-glass"></i>
          </button>
        </div>
        <div className="winfo">
          <img src={Cloud} alt="cloud" />
          <h1>{Math.round(data.celsius)}°C</h1>
          <h2>{data.name}</h2>
          {/* <div className="details">
            <div className="col">
              <div>
                <img src={User} alt="humidity" />
              </div>
              <div>
                <p>{data.feel}°C</p>
                <p>Feels like</p>
              </div>
            </div>
          </div> */}
          <div className="details">
            <div className="col">
              <div>
                <img src={Humidity} alt="humidity" />
              </div>
              <div>
                <p>{Math.round(data.humidity)}%</p>
                <p>Humidity</p>
              </div>
            </div>
            <div className="col">
              <div>
                <img src={Wind} alt="wind" />
              </div>
              <div>
                <p>{data.speed}km/h</p>
                <p>Wind</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="copyright">
        <a
          href="https://www.flaticon.com/free-icons/weather"
          title="weather icons"
        >
          Weather icons created by iconixar - Flaticon
        </a>
      </div>
    </div>
  );
}

export default Home;
