// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Cloud from "./assets/cloud.png";
import Clear from "./assets/sun.png";
import Rain from "./assets/rainy-day.png";
import Storm from "./assets/storm.png";
import Fog from "./assets/fog.png";
import Snow from "./assets/snowy.png";
import Cloudy from "./assets/cloudy.png";

function Details({ cityName, minTemp, maxTemp, image }) {
  // Zugriff auf die API-Schlüssel aus den Umgebungsvariablen
  const { REACT_APP_API_KEY } = process.env;

  // Ermitteln der Wochentage
  let daysOfWeek = ["So", "Mo", "Di", "Mi", "Do", "Fr", "Sa"];

  // Index des heutigen Wochentags
  let today = new Date().getDay();

  // Objekt zum Sammeln der Mindest- und Maximaltemperaturen für jeden Tag
  let tempsByDay = {};

  const [day2_minTemp, setDay2_minTemp] = useState("");
  const [day3_minTemp, setDay3_minTemp] = useState("");
  const [day4_minTemp, setDay4_minTemp] = useState("");
  const [day5_minTemp, setDay5_minTemp] = useState("");
  const [day6_minTemp, setDay6_minTemp] = useState("");

  const [day2_maxTemp, setDay2_maxTemp] = useState("");
  const [day3_maxTemp, setDay3_maxTemp] = useState("");
  const [day4_maxTemp, setDay4_maxTemp] = useState("");
  const [day5_maxTemp, setDay5_maxTemp] = useState("");
  const [day6_maxTemp, setDay6_maxTemp] = useState("");

  const [day2_name, setDay2Name] = useState("");
  const [day3_name, setDay3Name] = useState("");
  const [day4_name, setDay4Name] = useState("");
  const [day5_name, setDay5Name] = useState("");
  const [day6_name, setDay6Name] = useState("");

  const [day2_image, setDay2Image] = useState("");
  const [day3_image, setDay3Image] = useState("");
  const [day4_image, setDay4Image] = useState("");
  const [day5_image, setDay5Image] = useState("");
  const [day6_image, setDay6Image] = useState("");

  //console.log(cityName);
  const apiURL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

  axios
    .get(apiURL)
    .then((res) => {
      console.log(res.data.list);
      console.log(res.data.list[0].weather[0].main);

      // let apiDate = new Date(res.data.list[0].dt_txt).getDay(); // Index des Wochentags der API

      for (let i = 0; i < res.data.list.length; i++) {
        let apiDate = new Date(res.data.list[i].dt_txt).getDay();
        let minTempOfDay = res.data.list[i].main.temp_min;
        let maxTempOfDay = res.data.list[i].main.temp_max;
        let iconsOfDay = res.data.list[i].weather[0].main;

        // Vergleiche die Wochentage
        if (apiDate !== today) {
          let dayOfWeek = daysOfWeek[apiDate];
          // Überprüfe, ob bereits ein Eintrag für diesen Wochentag im Objekt existiert
          if (!tempsByDay[dayOfWeek]) {
            tempsByDay[dayOfWeek] = { min: [], max: [], icon: [] };
          }
          // Füge die Mindesttemperatur zum entsprechenden Tag hinzu
          tempsByDay[dayOfWeek].min.push(minTempOfDay);
          tempsByDay[dayOfWeek].max.push(maxTempOfDay);
          tempsByDay[dayOfWeek].icon.push(iconsOfDay);
        }
      }
      console.log("Mindesttemperaturen für jeden Wochentag:", tempsByDay);

      // Ermitteln der kleinsten Mindesttemp und der größten Maximaltemp
      // Iteriere über jeden Wochentag im tempsByDay-Objekt
      for (let i = 0; i < daysOfWeek.length; i++) {
        let day = daysOfWeek[i];

        // Wenn der Tag nach dem aktuellen Tag liegt
        if (i > today) {
          // Finde die kleinste und größte Temperatur für den aktuellen Tag
          let minTemp = tempsByDay[day].min[0];
          let maxTemp = tempsByDay[day].max[0];
          // let iconOfDay = tempsByDay[day].icon[0];

          for (let temp of tempsByDay[day].min) {
            if (temp < minTemp) {
              minTemp = temp;
            }
          }

          for (let temp of tempsByDay[day].max) {
            if (temp > maxTemp) {
              maxTemp = temp;
            }
          }

          for (let icon of tempsByDay[day].icon) {
            if (res.data.list[i].dt_txt.slice(-8) === "12:00:00") {
              icon = res.data.list[i].weather[0].main;
              console.log(icon);
            }
            // console.log(iconsOfDay);
          }

          //console.log(tempsByDay);

          // Icons extrahieren
          // Zugriff auf die Schlüssel des Objekts
          // Iteration über die ersten fünf Einträge

          const keys = Object.keys(tempsByDay);

          for (let i = 0; i < 5; i++) {
            //erster Eintrag = Tag
            const day = keys[i];
            switch (tempsByDay[day].icon) {
              case "Clouds":
                setDay2Image(Cloud);
                setDay3Image(Cloud);
                setDay4Image(Cloud);
                setDay5Image(Cloud);
                setDay6Image(Cloud);
                break;
              case "Clear":
                setDay2Image(Clear);
                setDay3Image(Clear);
                setDay4Image(Clear);
                setDay5Image(Clear);
                setDay6Image(Clear);
                break;
              case "Rain":
                setDay2Image(Rain);
                setDay3Image(Rain);
                setDay4Image(Rain);
                setDay5Image(Rain);
                setDay6Image(Rain);
                break;
              case "Storm":
                setDay2Image(Storm);
                setDay3Image(Storm);
                setDay4Image(Storm);
                setDay5Image(Storm);
                setDay6Image(Storm);
                break;
              case "Fog":
                setDay2Image(Fog);
                setDay3Image(Fog);
                setDay4Image(Fog);
                setDay5Image(Fog);
                setDay6Image(Fog);
                break;
              case "Snow":
                setDay2Image(Snow);
                setDay3Image(Snow);
                setDay4Image(Snow);
                setDay5Image(Snow);
                setDay6Image(Snow);
                break;
              default:
                setDay2Image(Cloudy);
                setDay3Image(Cloudy);
                setDay4Image(Cloudy);
                setDay5Image(Cloudy);
                setDay6Image(Cloudy);
                break;
            }
          }

          // Setze Usestates entsprechend der Tage
          switch (i) {
            case today + 1:
              setDay2_minTemp(minTemp);
              setDay2_maxTemp(maxTemp);
              setDay2Name(day);

              break;
            case today + 2:
              setDay3_minTemp(minTemp);
              setDay3_maxTemp(maxTemp);
              setDay3Name(day);
              break;
            case today + 3:
              setDay4_minTemp(minTemp);
              setDay4_maxTemp(maxTemp);
              setDay4Name(day);
              break;
            case today + 4:
              setDay5_minTemp(minTemp);
              setDay5_maxTemp(maxTemp);
              setDay5Name(day);
              break;
            case today + 5:
              setDay6_minTemp(minTemp);
              setDay6_maxTemp(maxTemp);
              setDay6Name(day);
              break;
            default:
              break;
          }
        }
