// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function Details({ cityName, minTemp, maxTemp, image }) {
  // Zugriff auf die API-Schlüssel aus den Umgebungsvariablen
  const { REACT_APP_API_KEY } = process.env;

  // const [days, setDays] = useState(today);
  let today = new Date();
  let day = today.toString().slice(0, 2);
  let day2 = "";
  let day3 = "";
  let day4 = "";
  let day5 = "";
  let day6 = "";
  let day7 = "";
  console.log("Heute: " + day);

  if (day === "Mo") {
    day2 = "Di";
    day3 = "Mi";
    day4 = "Do";
    day5 = "Fr";
    day6 = "Sa";
    day7 = "So";
  } else if (day === "Di") {
    day2 = "Mi";
    day3 = "Do";
    day4 = "Fr";
    day5 = "Sa";
    day6 = "So";
    day7 = "Mo";
  } else if (day === "Mi") {
    day2 = "Do";
    day3 = "Fr";
    day4 = "Sa";
    day5 = "So";
    day6 = "Mo";
    day6 = "Di";
  } else if (day === "Mi") {
    day2 = "Do";
    day3 = "Fr";
    day4 = "Sa";
    day5 = "So";
    day6 = "Mo";
    day7 = "Di";
  } else if (day === "Do") {
    day2 = "Fr";
    day3 = "Sa";
    day4 = "So";
    day5 = "Mo";
    day6 = "Di";
    day7 = "Mi";
  } else if (day === "Fr") {
    day2 = "Sa";
    day3 = "So";
    day4 = "Mo";
    day5 = "Di";
    day6 = "Mi";
    day7 = "Do";
  } else if (day === "Sa") {
    day2 = "So";
    day3 = "Mo";
    day4 = "Di";
    day5 = "Mi";
    day6 = "Do";
    day7 = "Fr";
  } else if (day === "So") {
    day2 = "Mo";
    day3 = "Di";
    day4 = "Mi";
    day5 = "Do";
    day6 = "Fr";
    day7 = "Sa";
  }

  //console.log(cityName);
  const apiURL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

  axios
    .get(apiURL)
    .then((res) => {
      console.log(res.data.list);
    })
    .catch((err) => {
      console.error("Fehler beim Abrufen der Wetterdaten:", err);
    });

  return (
    <>
      <div className="container_temp_infos">
        <h3>5-Tage-Vorhersage</h3>
        <div className="container_forecast">
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
          <div className="devider_forcast"></div>
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
        </div>
        <div id="tab"></div>
      </div>
    </>
  );
}

export default Details;
