// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";

function Details({ cityName, minTemp, maxTemp, image }) {
  // Zugriff auf die API-Schlüssel aus den Umgebungsvariablen
  const { REACT_APP_API_KEY } = process.env;

  // Ermitteln der Wochentage
  let daysOfWeek = ["So", "Mo", "Di", "Mi", "Do", "Fr", "Sa"];

  // Index des heutigen Wochentags
  let today = new Date().getDay();

  // Objekt zum Sammeln der Mindesttemperaturen für jeden Tag
  let minTempsByDay = {};

  const [day2_minTemp, setDay2_minTemp] = useState("");
  const [day3_minTemp, setDay3_minTemp] = useState("");
  const [day4_minTemp, setDay4_minTemp] = useState("");
  const [day5_minTemp, setDay5_minTemp] = useState("");
  const [day6_minTemp, setDay6_minTemp] = useState("");

  //console.log(cityName);
  const apiURL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

  axios
    .get(apiURL)
    .then((res) => {
      console.log(res.data.list);

      // let apiDate = new Date(res.data.list[0].dt_txt).getDay(); // Index des Wochentags der API

      for (let i = 0; i < res.data.list.length; i++) {
        let apiDate = new Date(res.data.list[i].dt_txt).getDay();
        let minTempOfDay = res.data.list[i].main.temp_min;

        // Vergleiche die Wochentage
        if (apiDate !== today) {
          let dayOfWeek = daysOfWeek[apiDate];
          // Überprüfe, ob bereits ein Eintrag für diesen Wochentag im Objekt existiert
          if (!minTempsByDay[dayOfWeek]) {
            minTempsByDay[dayOfWeek] = [];
          }
          // Füge die Mindesttemperatur zum entsprechenden Tag hinzu
          minTempsByDay[dayOfWeek].push(minTempOfDay);
        }
      }
      console.log("Mindesttemperaturen für jeden Wochentag:", minTempsByDay);

      // ermittelen der kleinsten Mindesttemp
      // Iteriere über jeden Wochentag im minTempsByDay-Objekt
      for (let day in minTempsByDay) {
        // Finde die kleinste Temperatur für den aktuellen Tag
        let minTemp = Math.min(...minTempsByDay[day]);
        // Überschreibe das Array mit einem Array, das nur die kleinste Temperatur enthält
        minTempsByDay[day] = [minTemp];
        // Setze Usestates
        switch (day) {
          case "Di":
            setDay2_minTemp(minTemp);
            break;
          case "Mi":
            setDay3_minTemp(minTemp);
            break;
          case "Do":
            setDay4_minTemp(minTemp);
            break;
          case "Fr":
            setDay5_minTemp(minTemp);
            break;
          case "Sa":
            setDay6_minTemp(minTemp);
            break;
          default:
            break;
        }
      }
    })
    .catch((err) => {
      console.error("Fehler beim Abrufen der Wetterdaten:", err);
    });

  return (
    <>
      <div className="container_temp_infos">
        <h3>5-Tage-Vorhersage</h3>
        <div className="container_forecast">
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
          <div className="devider_forcast"></div>
          <div className="forecast_row">
            <p></p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">{day2_minTemp}°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p></p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p></p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p></p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p></p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
          <div className="forecast_row">
            <p></p>
            <img className="forecast_icon" src="" alt="wetter icon" />
            <p className="minTemp">°C</p>
            <p>°C</p>
          </div>
        </div>
        <div id="tab"></div>
      </div>
    </>
  );
}

export default Details;
