// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";

// Funktion zur Ermittlung des Wochentags
function getWeekday(dt_txt) {
  const date = new Date(dt_txt);
  const weekdays = ["So", "Mo", "Di", "Mi", "Do", "Fr", "Sa"];
  return weekdays[date.getDay()];
}

function Details({ cityName, minTemp, maxTemp, image }) {
  // Zugriff auf die API-Schlüssel aus den Umgebungsvariablen
  const { REACT_APP_API_KEY } = process.env;

  // Zustände für die Wetterdaten, die Eingabestadt und Fehlermeldungen
  const [data, setData] = useState({
    celsius: 10,
    name: "Suche Stadt...",
    humidity: "",
    speed: "",
    image: "Cloud",
    feel: "",
    description: "",
    min_temp: "",
    max_temp: "",
    date: "",
    weekday: "",
  });

  //console.log(cityName);
  const apiURL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

  axios
    .get(apiURL)
    .then((res) => {
      console.log(res.data);
      res.data.list.forEach((element) => {
        const weekday = getWeekday(element.dt_txt);
        console.log("Wochentag:", weekday);
        // Hier könntest du weitere Verarbeitungsschritte für die Wetterdaten durchführen
        const groupedForecasts = {}; // Initialisiere das Objekt für gruppierte Vorhersagen

        for (const forecast of res.data.list) {
          const date = forecast.dt_txt.split(" ")[0]; // Nur das Datum extrahieren

          // Überprüfen, ob es bereits einen Eintrag für dieses Datum gibt, falls nicht, erstelle einen neuen
          if (!groupedForecasts[date]) {
            groupedForecasts[date] = [];
          }

          // Füge die aktuelle Vorhersage zur entsprechenden Gruppe hinzu
          groupedForecasts[date].push(forecast);
        }
        console.log(groupedForecasts);

        // Jetzt die Vorhersagen für jeden Wochentag um 12:00 Uhr filtern
        const forecastsAtNoon = {};
        for (const date in groupedForecasts) {
          // Filtere die Vorhersagen für jeden Wochentag um 12:00 Uhr
          const forecastsAtNoonForDay = groupedForecasts[date].filter(
            (forecast) => {
              return forecast.dt_txt.split(" ")[1] === "12:00:00";
            }
          );

          // Füge die gefilterten Vorhersagen zur neuen Gruppe hinzu
          if (forecastsAtNoonForDay.length > 0) {
            forecastsAtNoon[date] = forecastsAtNoonForDay;
          }
        }
      });
    })
    .catch((err) => {
      console.error("Fehler beim Abrufen der Wetterdaten:", err);
    });

  return (
    <>
      <div className="container_temp_infos">
        <h3>5-Tage-Vorhersage</h3>
        <div className="container_forecast">
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
          <div className="devider_forcast"></div>
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
        </div>
        <div id="tab"></div>
      </div>
    </>
  );
}

export default Details;
