// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { REACT_APP_API_KEY } from "../constants"; // Importiere die API-Schlüsselkonstante

function Details({ cityName }) {
  const [cityData, setCityData] = useState(null);

  useEffect(() => {
    if (cityName) {
      fetchData(cityName);
    }
  }, [cityName]);

  const fetchData = (cityName) => {
    const apiURL = `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

    axios
      .get(apiURL)
      .then((res) => {
        setCityData(res.data);
      })
      .catch((err) => {
        console.error("Error fetching city data:", err);
      });
  };

  if (!cityData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{cityData.name}</h2>
      <p>Temperature: {cityData.main.temp}°C</p>
      {/* Weitere Daten anzeigen */}
    </div>
  );
}

export default Details;
