// Details.js
import React, { useState, useEffect } from "react";
import axios from "axios";

// Funktion zur Ermittlung des Wochentags
function getWeekday(dt_txt) {
  const date = new Date(dt_txt);
  const weekdays = ["So", "Mo", "Di", "Mi", "Do", "Fr", "Sa"];
  return weekdays[date.getDay()];
}

function Details({ cityName, minTemp, maxTemp, image }) {
  // Zugriff auf die API-Schlüssel aus den Umgebungsvariablen
  const { REACT_APP_API_KEY } = process.env;

  // Zustand für die Wochentage
  const [nextDayForecast, setNextDayForecast] = useState(null);

  useEffect(() => {
    const apiURL = `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${REACT_APP_API_KEY}&units=metric&lang=de`;

    axios
      .get(apiURL)
      .then((res) => {
        const now = new Date(); // Aktuelles Datum und Uhrzeit
        const tomorrow = new Date(now); // Datum für morgen
        tomorrow.setDate(now.getDate() + 1); // Setze auf morgen
        tomorrow.setHours(12, 0, 0, 0); // Setze auf 12:00 Uhr

        // Finde die Vorhersage für morgen um 12:00 Uhr
        const forecastForTomorrow = res.data.list.find((forecast) => {
          const forecastDate = new Date(forecast.dt_txt);
          return (
            forecastDate.getDate() === tomorrow.getDate() &&
            forecastDate.getHours() === 12
          );
        });

        if (forecastForTomorrow) {
          setNextDayForecast(forecastForTomorrow);
        } else {
          setNextDayForecast(null);
        }
      })
      .catch((err) => {
        console.error("Fehler beim Abrufen der Wetterdaten:", err);
      });
  }, [cityName, REACT_APP_API_KEY]);

  return (
    <>
      <div className="container_temp_infos">
        <h3>5-Tage-Vorhersage</h3>
        <div className="container_forecast">
          <div className="forecast_row">
            <p>Heute</p>
            <img className="forecast_icon" src={image} alt="wetter icon" />
            <p className="minTemp">{Math.round(minTemp)}°C</p>
            <p>{Math.round(maxTemp)}°C</p>
          </div>
          <div className="devider_forcast"></div>
          {nextDayForecast && (
            <div className="forecast_row">
              <p>Morgen</p>
              <img
                className="forecast_icon"
                src={`https://openweathermap.org/img/w/${nextDayForecast.weather[0].icon}.png`}
                alt="wetter icon"
              />
              <p className="minTemp">
                {Math.round(nextDayForecast.main.temp_min)}°C
              </p>
              <p>{Math.round(nextDayForecast.main.temp_max)}°C</p>
            </div>
          )}
        </div>
        <div id="tab"></div>
      </div>
    </>
  );
}

export default Details;
