import "../style.css";
import Cloud from "./assets/cloud.png";
import Clear from "./assets/sun.png";
import Rain from "./assets/rainy-day.png";
import Storm from "./assets/storm.png";
import Fog from "./assets/fog.png";
import Snow from "./assets/snowy.png";
import Humidity from "./assets/weather.png";
import Wind from "./assets/wind.png";
import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  let lat = "";
  let lon = "";

  // function ermittlePosition() {
  //   const ausgabe = ausgabeRef.current; // current = aktueller wert
  //   if (navigator.geolocation) {
  //     navigator.geolocation.getCurrentPosition(zeigePosition, showError);
  //   } else {
  //     ausgabe.innerHTML = "Ihr Browser unterstützt keine Geolocation.";
  //   }
  // }
  const [data, setData] = useState(
    {
      celsius: 10,
      name: "London",
      humidity: 10,
      speed: 2,
      image: Cloud,
      feel: 10,
    },
    []
  );

  const [name, setName] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(zeigePosition, showError);
  }, []); // Leeres Array als Abhängigkeit bedeutet, dass der Effekt nur einmalig nach dem ersten Rendern ausgeführt wird

  function zeigePosition(position) {
    lat = position.coords.latitude;
    lon = position.coords.longitude;

    const apiURL = `http://api.openweathermap.org/geo/1.0/reverse?lat=${lat}&lon=${lon}&appid=95e1808808fc4f2e2fac242dc555bb96`;

    axios
      .get(apiURL)
      .then((res) => {
        const city = res.data.name;
        fetchData(city);
      })
      .catch((err) => {
        console.log(err);
        setError("Fehler beim Abrufen der Geolokationsdaten.");
        fetchData("London"); // Daten von London anzeigen, wenn Geolokation nicht verfügbar ist
      });
  }

  function showError(error) {
    console.log(error);
    setError("Geolokationsdienst nicht verfügbar.");
    fetchData("London"); // Daten von London anzeigen, wenn Geolokation nicht verfügbar ist
  }

  function fetchData(city) {
    const apiURL = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=95e1808808fc4f2e2fac242dc555bb96&units=metric&lang=de`;

    axios
      .get(apiURL)
      .then((res) => {
        let imagePath = Cloud;
        console.log(res.data);

        if (res.data.weather && res.data.weather.length > 0) {
          switch (res.data.weather[0].main) {
            case "Clouds":
              imagePath = Cloud;
              break;
            case "Clear":
              imagePath = Clear;
              break;
            case "Rain":
            case "Drizzle":
              imagePath = Rain;
              break;
            case "Mist":
              imagePath = Fog;
              break;
            case "Snow":
              imagePath = Snow;
              break;
            case "Thunderstorm":
              imagePath = Storm;
              break;
            default:
              imagePath = Cloud;
              break;
          }
        }

        setData({
          ...data,
          celsius: res.data.main.temp,
          name: res.data.name,
          humidity: res.data.main.humidity,
          speed: res.data.wind.speed,
          image: imagePath,
          feel: res.data.main.feels_like,
        });
        setError("");
      })
      .catch((err) => {
        console.log(err);
        setError(`Fehler beim Abrufen der Daten für ${city}.`);
      });
  }

  const handleClick = () => {
    if (name !== "") {
      fetchData(name);
    }
  };

  return (
    <div className="container">
      <div className="current_lo"></div>
      <div className="weather">
        <div className="search">
          <input
            id="input"
            type="text"
            placeholder="Enter City Name"
            onChange={(e) => setName(e.target.value)}
          />
          <button onClick={handleClick}>
            <i className="fa-solid fa-magnifying-glass"></i>
          </button>
        </div>
        <div className="winfo">
          <div className="error">
            <p>{error}</p>
          </div>
          <img src={data.image} alt="cloud" />
          <h1>{Math.round(data.celsius)}°C</h1>
          <h2>{data.name}</h2>
          <div className="details">
            <div className="col">
              <div>
                <img src={Humidity} alt="humidity" />
              </div>
              <div>
                <p>{Math.round(data.humidity)}%</p>
                <p>Humidity</p>
              </div>
            </div>
            <div className="col">
              <div>
                <img src={Wind} alt="wind" />
              </div>
              <div>
                <p>{Math.round(data.speed)}km/h</p>
                <p>Wind</p>
              </div>
            </div>
          </div>
        </div>
        <div className="copyright-mobil">
          <a
            href="https://www.flaticon.com/free-icons/weather"
            title="weather icons"
          >
            Weather icons created by iconixar - Flaticon
          </a>
        </div>
      </div>
      <div className="copyright">
        <a
          href="https://www.flaticon.com/free-icons/weather"
          title="weather icons"
        >
          Weather icons created by iconixar - Flaticon
        </a>
      </div>
    </div>
  );
}

export default Home;
