import React from "react";
// import axios from "axios";

function GeoLo() {
  const apiURL = `http://api.openweathermap.org/geo/1.0/reverse?lat={lat}&lon={lon}&limit={limit}&appid={API key}`;

  const button = document.getElementById("los"),
    ausgabe = document.getElementById("ausgabe");
  button.addEventListener("click", ermittlePosition);

  function ermittlePosition() {
    if (navigator.geolocation) {
      // wenn user erlaubt, dann
      navigator.geolocation.getCurrentPosition(zeigePosition);
    } else {
      ausgabe.innerHTML = "Ihr Browser unterst체tzt keine Geolocation.";
    }
  }

  function zeigePosition(position) {
    ausgabe.innerHTML =
      "Ihre Koordinaten sind:<br> Breite: " +
      position.coords.latitude +
      "<br>L채nge: " +
      position.coords.longitude;
  }

  return (
    <div>
      <p>
        Sobald Sie auf den Button klicken, werden Ihre Koordinaten ermittelt.
      </p>
      <button id="los">Los!</button>
      <p id="ausgabe"></p>
    </div>
  );
}

export default GeoLo;
